mapper.select:
select t1.id, t1.template_id, t1.assembly_title, t1.assembly_name, t1.assembly_type, t1.data_type, t1.degree, t1.x, t1.y, t1.width, t1.height, t1.collection, t2.id as data_id, t2.data_type, t2.data_legend, t2.assembly_data, t2.x_mapping, t2.y_mapping from t_assembly t1 left join t_assembly_data t2 on t1.id = t2.assembly_id and t1.data_type = t2.data_type where 1 = 1
and t1.template_id = #{templateId,jdbcType=VARCHAR}
and t1.id = #{id,jdbcType=VARCHAR}

mapper.insert:

  select replace(uuid(),"-","")

insert into t_assembly (id, template_id, assembly_title, assembly_name,
  assembly_type, data_type, degree, 
  x, y, width, height, collection
  )
values (#{id,jdbcType=VARCHAR}, #{templateId,jdbcType=VARCHAR}, #{assemblyTitle,jdbcType=VARCHAR},
  #{assemblyName,jdbcType=VARCHAR}, #{assemblyType,jdbcType=INTEGER}, #{dataType,jdbcType=INTEGER},
  #{degree,jdbcType=INTEGER}, #{x,jdbcType=INTEGER}, #{y,jdbcType=INTEGER}, #{width,jdbcType=INTEGER},
  #{height,jdbcType=INTEGER}, #{collection,jdbcType=INTEGER}
  )

insert into t_assembly (id, template_id, assembly_title, assembly_name,
assembly_type, data_type, degree,
x, y, width, height
)
values

  (#{assemblyEntity.id,jdbcType=VARCHAR}, #{assemblyEntity.templateId,jdbcType=VARCHAR}, #{assemblyTitle,jdbcType=VARCHAR}, #{assemblyEntity.assemblyName,jdbcType=VARCHAR},
  #{assemblyEntity.assemblyType,jdbcType=INTEGER}, #{assemblyEntity.dataType,jdbcType=INTEGER}, #{assemblyEntity.degree,jdbcType=INTEGER},
  #{assemblyEntity.x,jdbcType=INTEGER}, #{assemblyEntity.y,jdbcType=INTEGER}, #{assemblyEntity.width,jdbcType=INTEGER}, #{assemblyEntity.height,jdbcType=INTEGER}
  )
